css:
.github-CommitDetailView {
  display: flex;
  flex-direction: column;
  height: 100%;
}
.github-CommitDetailView-header {
  flex: 0;
  border-bottom: 1px solid #181d23;
  background-color: #1D252C;
}
.github-CommitDetailView-commit {
  padding: 32px;
  padding-bottom: 0;
}
.github-CommitDetailView-title {
  margin: 0 0 .25em 0;
  font-size: 1.4em;
  line-height: 1.3;
  color: #b7c5d3;
}
.github-CommitDetailView-avatar {
  border-radius: 0px;
  height: 16px;
  margin-right: .4em;
  width: 16px;
}
.github-CommitDetailView-meta {
  display: flex;
  align-items: center;
  margin: 8px 0 32px 0;
}
.github-CommitDetailView-metaText {
  flex: 1;
  margin-left: 22.4px;
  line-height: 16px;
  color: #41505e;
}
.github-CommitDetailView-moreButton {
  position: absolute;
  left: 50%;
  transform: translate(-50%, -50%);
  padding: 0em .4em;
  color: #41505e;
  font-style: italic;
  border: 1px solid #1E2329;
  border-radius: 0px;
  background-color: #333F4A;
}
.github-CommitDetailView-moreButton:hover {
  background-color: #3f515f;
}
.github-CommitDetailView-moreText {
  padding: 32px 0;
  font-size: inherit;
  font-family: var(--editor-font-family);
  word-wrap: initial;
  word-break: break-word;
  white-space: pre-wrap;
  border-top: 1px solid #181d23;
  background-color: transparent;
  max-height: 55vh;
}
.github-CommitDetailView-moreText:empty {
  display: none;
}
.github-CommitDetailView-sha {
  flex: 0 0 7ch;
  margin-left: 32px;
  line-height: 16px;
  color: #41505e;
  font-family: var(--editor-font-family);
  white-space: nowrap;
  overflow: hidden;
}
.github-CommitDetailView-sha a {
  color: #539afc;
}
